mapper.select:
SELECT rb_id AS rbId, rb_title AS rbTitle, rb_content AS rbContent, rb_view_cnt AS rbViewCnt, rb_like_cnt AS rbLikeCnt, rb_register_date AS rbRegisterDate, rb_update_date AS rbUpdateDate, rb_writer AS rbWriter, rb_notice_yn AS rbNoticeYn, rb_vaccine_kind AS rbVaccineKind FROM review_board
AND rb_title LIKE CONCAT('%', #{rbInput}, '%')
AND rb_content LIKE CONCAT('%', #{rbInput}, '%')
AND rb_vaccine_kind = #{rbVaccineKind}
LIMIT 9 OFFSET ${startRow}
